      y: rPos.position.y,
      category: 'root',
      symbol: 'circle',
      symbolSize: rPos.symbolSize
    };
    data.push(rootNodeOptions);
    let visited = [];
    let queue = [];
    queue.push(root);

    while (queue.length > 0) {
