              itemStyle: {
                color: 'red',
                opacity: 1
              }
            }
          };
          data.push(nodeItemOptions);
          queue.push(n);
        }
      }
    }

    if (this.selectedNodeName != "" && !this.allConnectionsActive) {
      let node = website.map.get(this.selectedNodeName);

      for (let i = 0; i < node.intLinks.length; i++) {
        let n = website.map.get(node.intLinks[i]);
        let label = {
          position: 'start',
          distance: 10
        };
        let doubleSided = false;

        if (n != undefined) {
          n.intLinks.forEach(element => {
            if (element == node.url) {
              doubleSided = true;
            }
          });
        }

        let symbol = [];
        let symbolSize = [];

        if (doubleSided) {
          symbol = ['arrow', 'arrow'];
          symbolSize = [5, 5];
        } else {
          symbol = ['none', 'arrow'];
          symbolSize = [0, 5];
        }

        let graphEdgeOption = {
          lineStyle: this.nodeConnectionStyle,
          label: label,
          source: node.url,
          target: node.intLinks[i],
          symbol: symbol,
          symbolSize: symbolSize,
          value: 3
        };
        linkData.push(graphEdgeOption);
      }
    }

    let graphData = new GraphData(data, linkData);
    return graphData;
  }

  calculatePositions(website, root) {
    let rootPosition = this.calculatePosition(0, 1, 1, 0, Math.PI * 2);
    let rootSize = this.getSymbolSize(root.depth, Math.PI * 2);
    root.nodePositionalInformation = {
      position: rootPosition,
      symbolSize: rootSize,
      rangeFrom: 0,
      rangeTo: Math.PI * 2
    };
    let visited = [];
    let queue = [];
    queue.push(root);

    while (queue.length > 0) {
      let currentNode = queue.shift();
      visited.push(currentNode);
      let domChilds = currentNode.domChilds;
      let radiansPerChild = (currentNode.nodePositionalInformation.rangeTo - currentNode.nodePositionalInformation.rangeFrom) / domChilds.length;
      let currentRadians = 0;

      for (let i = 0; i < domChilds.length; i++) {
        let n = website.map.get(domChilds[i]);

        if (!visited.includes(n)) {
          let symbolSize = this.getSymbolSize(n.depth, radiansPerChild);
          let nodePosition = this.calculatePosition(n.depth, i, domChilds.length, currentNode.nodePositionalInformation.rangeFrom, currentNode.nodePositionalInformation.rangeTo);
          let rangeFrom = currentNode.nodePositionalInformation.rangeFrom + currentRadians - radiansPerChild * 0.5;
          currentRadians += radiansPerChild;
          let rangeTo = currentNode.nodePositionalInformation.rangeFrom + currentRadians - radiansPerChild * 0.5;
          n.nodePositionalInformation = {
            position: nodePosition,
            symbolSize: symbolSize,
            rangeFrom: rangeFrom,
            rangeTo: rangeTo
          };
          queue.push(n);
        }
      }
    }
  }

