                    }
                    else if(key.includes(':int:'))
                    {
                      linksInt.push(value);
                    }
                    else if(key.includes(':ext:media:'))
                    {
                      mediaExt.push(value);
                    }
                    else if(key.includes(':ext:'))
                    {
                      linksExt.push(value);
                    }
                    else if(key.includes('url'))
                    {
                      url = value;
                    }
                    
                  }
                );
                
                let n : NodeFromJson = new NodeFromJson();
                n.url = url;
                n.parentDocId = pdi;
                n.docId = di;
                n.extMedia = mediaExt;
                n.intMedia = mediaInt;
                n.extLinks = linksExt;
                n.intLinks = linksInt;
          
                n.getDepth();
                n.getDOMParent();
