class NodeFromJson {
  constructor() {
    this.domParent = "";
    this.domChilds = [];
    this.rangeFrom = 0;
    this.rangeTo = Math.PI * 2;
  }

  getDOMStripedName() {
    if (this.depth <= 0 || this.url.length <= 0) return this.url;

    for (let index = this.url.length - 1; index >= 0; index--) {
      const element = this.url[index];

      if (element == '/') {
        return this.url.slice(index);
      }
    }

    return "";
  }

  getDOMParent() {
    if (this.depth <= 0) return;
    let slashCount = this.depth + 2;

    if ("this.url.includes('.html')") {
      slashCount--;
    }

    for (let index = 0; index < this.url.length; index++) {
      const element = this.url[index];

      if (element == '/') {
        slashCount--;
      }

      if (slashCount == 0) {
        this.domParent = this.url.substring(0, index + 2);
      }
    }
  }

  getDepth() {
    let count = -3;

    for (let index = 0; index < this.url.length; index++) {
      const element = this.url[index];

      if (element == '/') {
        count++;
      }
    }

    if (this.url.includes('.html') || this.url.includes('.shtml')) {
      count++;
    }

    this.depth = count;
  }

}

class WebsiteMap {
  constructor(t_name, t_map, t_internalMedia) {
    this.name = t_name;
    this.map = t_map;
    this.internalMedia = t_internalMedia;
  }

}

export { NodeFromJson };

