import { graphic } from 'echarts';
import * as data from '../../../assets/tagesschauNew.json';
import { NodeInfoComponent } from 'src/app/node-info/node-info.component';
import * as i0 from "@angular/core";
import * as i1 from "src/app/website.service";
function SimplegraphComponent_li_23_Template(rf, ctx) { if (rf & 1) {
    i0.ɵɵelementStart(0, "li");
    i0.ɵɵtext(1);
    i0.ɵɵelementEnd();
} if (rf & 2) {
    const website_r1 = ctx.$implicit;
    i0.ɵɵadvance(1);
    i0.ɵɵtextInterpolate1(" ", website_r1, " ");
} }
/*
considerations for node placement:
- for first layer: for all childs of root, sum up all their children. Then try to find a configuration,
where the 'center of mass' is at the root node. (adding up vectors * weight should equal 0 at the end)
- might not work, because a valid strategy for that would also be to just dump all of the nodes at opposite ends

- after finishing a whole depth layer, there might be a lot of space left unoccupied in that layer
- the childnoderanges for the layer before could be accordingly ajusted to use up the whole 360°
- (or atleast space them out more)

*/
export class SimplegraphComponent {
    constructor(websiteService) {
        this.websiteService = websiteService;
        this.totalWidth = 1000;
        this.halfWidth = 500;
        this.totalHeight = 1000;
        this.halfHeight = 500;
        this.nodeSizeFalloff = 200;
        this.radiusPerIteration = 100;
        this.websiteNameForGraph = "";
        this.websiteNameForCrawler = "";
        this.loading = false;
        this.crawledWebsiteID = 2;
        this.nodeConnectionStyle = {
            curveness: 0,
            width: 1,
            color: 'grey',
            opacity: 0.8,
            type: 'dashed',
            cap: 'square',
            join: 'bevel',
            dashOffset: 10,
            miterLimit: 10,
        };
        this.domChildConnectionStyle = {
            curveness: 0,
            width: 2,
            color: 'blue',
            opacity: 0.8,
            type: 'solid',
            cap: 'square',
            join: 'bevel',
            dashOffset: 10,
            miterLimit: 10,
        };
        this.selectedNodeName = "";
        //initTestData() : Map<string, NodeFromJson>
        this.domConnectionsActive = true;
        this.allConnectionsActive = false;
        this.labelsActive = false;
    }
    /*
    ngOnInit(): void
    {
      this.prepareGraph();
      this.showGraph();
    }*/
    remap(n, oldFrom, oldTo, newFrom, newTo) {
        let oldRange = oldTo - oldFrom;
        let proportion = (n - oldFrom) / oldRange;
        let newRange = newTo - newFrom;
        return newFrom + newRange * proportion;
    }
    calculatePosition(depth, index, total, rangeFrom, rangeTo) {
        let radians = (total == 1) ? 0.5 * 2 * Math.PI : (index / total) * 2 * Math.PI;
        let radiansRemapped = this.remap(radians, 0, Math.PI * 2, rangeFrom, rangeTo);
        let x = Math.cos(radiansRemapped) * depth * this.radiusPerIteration;
        let y = Math.sin(radiansRemapped) * depth * this.radiusPerIteration;
        let pos = new V2(x, y);
        return this.calculateOffset(pos);
    }
    calculateOffset(pos) {
        return new V2(pos.x + this.halfWidth, pos.y + this.halfHeight);
    }
    getSymbolSize(currentDepth, radiansAvailable) {
        let frac = radiansAvailable / (Math.PI * 2);
        let n = 20 * frac * frac;
        if (frac < 0.01) {
            console.log('xxxx');
        }
        let size = 20 * (1 / (currentDepth + 1));
        return size;
    }
    symbolCallback(rawValue, params) {
        console.log("Test");
        return 1;
    }
    calculatePositions(website, root) {
        let data = [];
        let linkData = [];
        let rootPosition = this.calculatePosition(0, 1, 1, root.rangeFrom, root.rangeTo);
        let rootNodeOptions = {
            name: root.url,
            x: rootPosition.x,
            y: rootPosition.y,
            category: 'root',
            symbol: 'circle',
            symbolSize: this.getSymbolSize(root.depth, Math.PI * 2)
        };
        data.push(rootNodeOptions);
        let visited = [];
        let queue = [];
        queue.push(root);
        while (queue.length > 0) {
            let currentNode = queue.shift();
            visited.push(currentNode);
            let domChilds = currentNode.domChilds;
            let radiansPerChild = (currentNode.rangeTo - currentNode.rangeFrom) / domChilds.length;
            let currentRadians = 0;
            for (let i = 0; i < domChilds.length; i++) {
                let n = website.map.get(domChilds[i]);
                //show links to domChilds
                if (this.domConnectionsActive) {
                    let label = {
                        position: 'start',
                        distance: 10
                    };
                    let graphEdgeOption = {
                        lineStyle: this.domChildConnectionStyle,
                        label: label,
                        source: currentNode.url,
                        target: n.url,
                        symbol: ['none', 'arrow'],
                        symbolSize: [0, 10],
                        value: 3
                    };
                    linkData.push(graphEdgeOption);
                }
                if (!visited.includes(n)) {
                    //let domName = n.getDOMStripedName();
                    let nodePosition = this.calculatePosition(n.depth, i, domChilds.length, currentNode.rangeFrom, currentNode.rangeTo);
                    let nodeItemOptions = {
                        name: n.url,
                        x: nodePosition.x,
                        y: nodePosition.y,
                        category: 'not root',
                        draggable: true,
                        symbol: 'circle',
                        symbolSize: this.getSymbolSize(n.depth, radiansPerChild),
                        symbolOffset: 0,
                        symbolRotate: this.symbolCallback("", "")
                    };
                    data.push(nodeItemOptions);
                    n.rangeFrom = currentNode.rangeFrom + currentRadians - radiansPerChild * 0.5;
                    currentRadians += radiansPerChild;
                    n.rangeTo = currentNode.rangeFrom + currentRadians - radiansPerChild * 0.5;
                    queue.push(n);
                }
            }
        }
        if (this.selectedNodeName != "") {
            let node = website.map.get(this.selectedNodeName);
            for (let i = 0; i < node.intLinks.length; i++) {
                let n = website.map.get(node.intLinks[i]);
                let label = {
                    position: 'start',
                    distance: 10
                };
                let doubleSided = false;
                if (n != undefined) {
                    n.intLinks.forEach(element => {
                        if (element == node.url) {
                            doubleSided = true;
                        }
                    });
                }
                let symbol = [];
                let symbolSize = [];
                if (doubleSided) {
                    symbol = ['arrow', 'arrow'];
                    symbolSize = [10, 10];
                }
                else {
                    symbol = ['none', 'arrow'];
                    symbolSize = [0, 10];
                }
                let graphEdgeOption = {
                    lineStyle: this.nodeConnectionStyle,
                    label: label,
                    source: node.url,
                    target: node.intLinks[i],
                    symbol: symbol,
                    symbolSize: symbolSize,
                    value: 3
                };
                linkData.push(graphEdgeOption);
            }
        }
        let graphData = new GraphData(data, linkData);
        return graphData;
    }
    onChartMouseOver(ec) {
        //console.log("hover on " + ec.name);
    }
    onChartClick(ec) {
        let n = this.website.map.get(ec.name);
        if (n != undefined) {
            this.selectedNodeName = n.url;
            this.prepareGraph();
            this.showGraph();
            this.host.node = n;
            this.host.nodeName = n.url;
            this.showIntMedia(n.intMedia);
            return;
        }
    }
    showAllConnections() {
        this.allConnectionsActive = !this.allConnectionsActive;
        this.prepareGraph();
        this.showGraph();
    }
    showConnections() {
        this.domConnectionsActive = !this.domConnectionsActive;
        this.showGraph();
    }
    showLabels() {
        this.labelsActive = !this.labelsActive;
        this.showGraph();
    }
    deselectAll() {
        this.showIntMedia(this.website.internalMedia);
    }
    prepareWebsite(name) {
        let dictElements = JSON.parse(JSON.stringify(data));
        let nodeMap = new Map();
        let mediaIntGlobal = [];
        for (let index = 0; index < dictElements.length; index++) {
            const element = dictElements[index];
            let n = new NodeFromJson();
            if (element.url == undefined) {
                continue;
            }
            n.url = element.url;
            n.parentDocId = element.parentDocId;
            n.docId = element.docId;
            n.extMedia = element['link:ext:media'];
            n.intMedia = element['link:int:media'];
            n.extLinks = element['link:ext'];
            n.intLinks = element['link:int'];
            n.getDepth();
            n.getDOMParent();
            if (n.depth >= 0) {
                nodeMap.set(n.url, n);
            }
        }
        nodeMap.forEach((node, key) => {
            if (node.domParent != "") {
                let parent = nodeMap.get(node.domParent);
                if (parent != undefined) {
                    parent.domChilds.push(key);
                }
            }
        });
        let website = new WebsiteMap(name, nodeMap, mediaIntGlobal);
        console.log("Website Node nach dem Init View:", website);
        return website;
    }
    mapTo2DMap(media) {
        let ret = [];
        let rowDict = [];
        let column = 0;
        media.forEach((url) => {
            rowDict.push(url);
            column++;
            if (column > 5) {
                column = 0;
                ret.push(rowDict);
                rowDict = [];
            }
        });
        console.log(ret);
        return ret;
    }
    showIntMedia(media) {
        let transformed = this.mapTo2DMap(media);
        this.host.images = transformed;
    }
    /**
     * The method fetches, saves and prepares the data for the Creation of the Website Graph. Approximately 200 Nodes,
     * that were crawled by the Worker and saved into Redis, get fetched. The data is saved into dictionaries, adapting to
     * the Data structure that was created in the Backend. Finally, the Nodes for the Graph are created.
     */
    async initRealData() {
        /*
        let nodeMap : Map<string, NodeFromJson> = new Map<string, NodeFromJson>();
        this.loading = true;
        this.errorMessage = "";
        this.websiteService.getWebsiteNodes(this.websiteNameForGraph)
          .subscribe(
            response => {                           //next() callback
              console.log('response received')
              //this.websites = response;
              let dictElements : {[index:string] : string}[] = JSON.parse(JSON.stringify(response))
         
              for (let index = 0; index < dictElements.length; index++) {
                const element  : {[index:string] : string} = dictElements[index];
          
                let pdi : string = "";
                let di : string = "";
