            () => {                                   //complete() callback
              console.error('Request completed')      //This is actually not needed
              this.loading = false;
            })
            */
    }
    /**
     * The User tells the Server which Website it should crawl next, so a Graph can be vusialized later on for
     * this Website. The POST-Request to the Server is initialized and the Answer of the Server is read and told
     * to the User. For that purpose, the crawlWebsite-Method returns an Observable which is subscribed upon.
     */
    addWebsiteToCrawler() {
        this.crawledWebsiteID++;
        this.websiteService.crawlWebsite(this.websiteNameForCrawler, this.crawledWebsiteID).subscribe({
            next: data => {
                this.postId = data.id;
                console.log(data);
                alert(data.body[0].toString() + " is now starting to get crawled!");
            },
            error: error => {
                this.errorMessage = error.error;
                console.error('There was an error!', error);
                alert(this.errorMessage);
            }
        });
    }
    /**Restart Crawler in the Worker */
    crawlNewWebsites() {
        this.websiteService.startNewCrawlers().subscribe({
            next: data => {
                this.postId = data.id;
                console.log(data);
            },
            error: error => {
                this.errorMessage = error.error;
                console.error('There was an error!', error);
                alert(this.errorMessage);
            }
        });
    }
    getCrawledWebsites() {
        this.loading = true;
        this.errorMessage = "";
        this.websiteService.getCrawledWebsites()
            .subscribe((response) => {
            console.log('response received');
            this.crawledWebsites = response;
        }, (error) => {
            console.error('Request failed with error');
            this.errorMessage = error;
            this.loading = false;
        }, () => {
            console.error('Request completed'); //This is actually not needed 
            this.loading = false;
        });
    }
    ngOnInit() {
        this.getCrawledWebsites();
        this.prepareGraph();
        this.showGraph();
    }
    getWebsiteGraph() {
        //prepare Website-Method with Rest Call ->
        let nodeMap = new Map();
        this.loading = true;
        this.errorMessage = "";
        this.websiteService.getWebsiteNodes(this.websiteNameForGraph)
            .subscribe(data => {
            console.log('response received');
            let dictElements = JSON.parse(JSON.stringify(data));
            let mediaIntGlobal = [];
            for (let index = 0; index < dictElements.length; index++) {
                const element = dictElements[index];
                let n = new NodeFromJson();
                if (element.url == undefined) {
                    continue;
                }
                n.url = element.url;
                n.parentDocId = element.parentDocId;
                n.docId = element.docId;
                n.extMedia = element['link:ext:media'];
                n.intMedia = element['link:int:media'];
                n.extLinks = element['link:ext'];
                n.intLinks = element['link:int'];
                n.getDepth();
                n.getDOMParent();
                if (n.depth >= 0) {
                    nodeMap.set(n.url, n);
                }
            }
            nodeMap.forEach((node, key) => {
                if (node.domParent != "") {
                    let parent = nodeMap.get(node.domParent);
                    if (parent != undefined) {
                        parent.domChilds.push(key);
                    }
                }
            });
            this.website = new WebsiteMap(this.websiteNameForGraph, nodeMap, mediaIntGlobal);
            console.log("Website Node nach dem Click:", this.website);
            //prepareWebsite-Method ->
