  getOffset(websiteIndex, total) {
    let frac = websiteIndex / total * TAU;
    let x = Math.cos(frac) * websiteSpacing;
    let y = Math.sin(frac) * websiteSpacing;
    return new V2(x, y);
  }

  prepareWebsite(name, data, websiteIndex, total) {
    let dictElements = JSON.parse(data);
    let nodeMap = new Map();
    let rootName = "";
    let mediaIntGlobal = [];

    for (let index = 0; index < dictElements.length; index++) {
      const element = dictElements[index]; //root

      if (index == 0) {
        rootName = element.url;
      }

      let n = new NodeFromJson();

      if (element.url == undefined) {
        continue;
      }

      n.url = element.url;
      n.parentDocId = element.parentDocId;
      n.docId = element.docId;
      n.extMedia = element['link:ext:media'];
      n.intMedia = element['link:int:media'];
      n.extLinks = element['link:ext'];
      n.intLinks = element['link:int'];
      n.getDepth();
      n.getDOMParent();

      if (n.depth >= 0) {
        nodeMap.set(n.url, n);
      }
    }

    nodeMap.forEach((node, key) => {
      if (node.domParent != "") {
        let parent = nodeMap.get(node.domParent);

        if (parent != undefined) {
          parent.domChilds.push(key);
        }
      }
    });
    let website = new WebsiteMap(name, nodeMap, mediaIntGlobal);
    website.rootName = rootName;
    website.offset = this.getOffset(websiteIndex, total);
    this.websiteRegister.map.set(name, website); //return website;
  }
