import * as data from '../../../assets/tagesschauNew.json';
import { NodeInfoComponent } from 'src/app/node-info/node-info.component';
import { WebsiteRegister, GraphData, V2, NodeFromJson, WebsiteMap } from 'src/app/graphData';
import * as i0 from "@angular/core";
import * as i1 from "src/app/website.service";
import * as i2 from "@angular/forms";
import * as i3 from "../button/button.component";
import * as i4 from "ngx-echarts";
import * as i5 from "@angular/common";
import * as i6 from "../../node-info/node-info.component";
function SimplegraphComponent_li_21_Template(rf, ctx) { if (rf & 1) {
    i0.ɵɵelementStart(0, "li");
    i0.ɵɵtext(1);
    i0.ɵɵelementEnd();
} if (rf & 2) {
    const website_r1 = ctx.$implicit;
    i0.ɵɵadvance(1);
    i0.ɵɵtextInterpolate1(" ", website_r1, " ");
} }
const spacingThreshhold = 0.007;
const outerColor = 'purple';
const innerColor = 'red';
const innerColorHover = 'orange';
const selectedColorInner = '#fff';
const websiteSpacing = 1000;
const TAU = 6.283185307179586;
/*
considerations for node placement:
- for first layer: for all childs of root, sum up all their children. Then try to find a configuration,
where the 'center of mass' is at the root node. (adding up vectors * weight should equal 0 at the end)
- might not work, because a valid strategy for that would also be to just dump all of the nodes at opposite ends

- after finishing a whole depth layer, there might be a lot of space left unoccupied in that layer
- the childnoderanges for the layer before could be accordingly ajusted to use up the whole 360°
- (or atleast space them out more)

*/
export class SimplegraphComponent {
    constructor(websiteService) {
        this.websiteService = websiteService;
        this.totalWidth = 1000;
        this.halfWidth = 500;
        this.totalHeight = 1000;
        this.halfHeight = 500;
        this.nodeSizeFalloff = 200;
        this.radiusPerIteration = 100;
        this.websiteNameForGraph = "";
        this.websiteNameForCrawler = "";
        this.loading = false;
        this.crawledWebsiteID = 2;
        this.nodeConnectionStyle = {
            curveness: 0,
            width: 1,
            color: 'grey',
            opacity: 0.8,
            type: 'dashed',
            cap: 'square',
            join: 'bevel',
            dashOffset: 10,
            miterLimit: 10,
        };
        this.domChildConnectionStyle = {
            curveness: 0,
            width: 2,
            color: 'blue',
            opacity: 0.8,
            type: 'solid',
            cap: 'square',
            join: 'bevel',
            dashOffset: 10,
            miterLimit: 10,
        };
        this.selectedNodeName = "";
        this.domConnectionsActive = true;
        this.allConnectionsActive = false;
        this.labelsActive = false;
    }
    remap(n, oldFrom, oldTo, newFrom, newTo) {
        let oldRange = oldTo - oldFrom;
        let proportion = (n - oldFrom) / oldRange;
        let newRange = newTo - newFrom;
        return newFrom + newRange * proportion;
    }
    calculatePosition(depth, index, total, rangeFrom, rangeTo, graphOffset) {
        let radians = (total == 1) ? 0.5 * TAU : (index / total) * TAU;
        let radiansRemapped = this.remap(radians, 0, TAU, rangeFrom, rangeTo);
        let x = Math.cos(radiansRemapped) * depth * this.radiusPerIteration + graphOffset.x;
        let y = Math.sin(radiansRemapped) * depth * this.radiusPerIteration + graphOffset.y;
        let pos = new V2(x, y);
        return this.calculateOffset(pos);
    }
    calculateOffset(pos) {
        return new V2(pos.x + this.halfWidth, pos.y + this.halfHeight);
    }
    getSymbolSize(currentDepth, radiansAvailable) {
        let frac = radiansAvailable / TAU;
        let multiplier = 1;
        if (frac < spacingThreshhold) {
            multiplier = frac / spacingThreshhold;
        }
        let size = 20 * (1 / (currentDepth + 1)) * multiplier;
        return size;
    }
    getGraphData(websiteReg) {
        let data = [];
        let linkData = [];
        websiteReg.map.forEach((value, key) => {
            let root = value.map.get(value.rootName);
            let rPos = root.nodePositionalInformation;
            let rootNodeOptions = {
                name: root.url,
                x: rPos.position.x,
                y: rPos.position.y,
                category: 'root',
                symbol: 'circle',
                symbolSize: rPos.symbolSize
            };
            data.push(rootNodeOptions);
            let visited = [];
            let queue = [];
            queue.push(root);
            while (queue.length > 0) {
                let currentNode = queue.shift();
                visited.push(currentNode);
                let domChilds = currentNode.domChilds;
                if (this.allConnectionsActive) {
                    let intLinks = currentNode.intLinks;
                    for (let i = 0; i < intLinks.length; i++) {
                        let n = value.map.get(intLinks[i]);
                        if (n == undefined)
                            continue;
                        let label = {
                            position: 'start',
                            distance: 10
                        };
                        let graphEdgeOption = {
                            lineStyle: this.nodeConnectionStyle,
                            label: label,
                            source: currentNode.url,
                            target: n.url,
                            symbol: ['none', 'arrow'],
                            symbolSize: [0, 5],
                            value: 3
                        };
                        linkData.push(graphEdgeOption);
                    }
                }
                for (let i = 0; i < domChilds.length; i++) {
                    let n = value.map.get(domChilds[i]);
                    let posInfo = n.nodePositionalInformation;
                    let arrowSize = n.nodePositionalInformation.symbolSize * 0.7;
                    //show links to domChilds
                    if (this.domConnectionsActive) {
                        let label = {
                            position: 'start',
                            distance: 10
                        };
                        let graphEdgeOption = {
                            lineStyle: this.domChildConnectionStyle,
                            label: label,
                            source: currentNode.url,
                            target: n.url,
                            symbol: ['none', 'arrow'],
                            symbolSize: [0, arrowSize],
                            value: 3
                        };
                        linkData.push(graphEdgeOption);
                    }
                    //add node if it doesnt already exist
                    if (!visited.includes(n)) {
                        let color = innerColor;
                        if (this.selectedNodeName == n.url) {
                            color = selectedColorInner;
                        }
                        //let domName = n.getDOMStripedName();
                        let nodeItemOptions = {
                            name: n.url,
                            x: posInfo.position.x,
                            y: posInfo.position.y,
                            category: 'not root',
                            draggable: false,
                            symbol: 'circle',
                            symbolSize: posInfo.symbolSize,
                            symbolOffset: 0,
                            symbolRotate: 0,
                            itemStyle: {
                                color: {
                                    type: 'radial',
                                    x: 0.5,
                                    y: 0.5,
                                    r: 0.5,
                                    colorStops: [{
                                            offset: 0.0,
                                            color: color
                                        },
                                        {
                                            offset: 1.0,
                                            color: outerColor
                                        }
                                    ],
                                    global: false
                                },
                                opacity: 1
                            },
                            emphasis: {
                                itemStyle: {
                                    color: {
                                        type: 'radial',
                                        x: 0.5,
                                        y: 0.5,
                                        r: 0.5,
                                        colorStops: [{
                                                offset: 0.0,
                                                color: innerColorHover
                                            },
                                            {
                                                offset: 1.0,
                                                color: outerColor
                                            }
                                        ],
                                        global: false
                                    },
                                    opacity: 1,
                                },
                            },
                        };
                        data.push(nodeItemOptions);
                        queue.push(n);
                    }
                }
            }
            if (this.selectedNodeName != "" && !this.allConnectionsActive) {
                let node = value.map.get(this.selectedNodeName);
                for (let i = 0; i < node.intLinks.length; i++) {
                    let n = value.map.get(node.intLinks[i]);
                    let label = {
                        position: 'start',
                        distance: 10
                    };
                    let doubleSided = false;
                    if (n != undefined) {
                        n.intLinks.forEach(element => {
                            if (element == node.url) {
                                doubleSided = true;
                            }
                        });
                    }
                    let symbol = [];
                    let symbolSize = [];
                    if (doubleSided) {
                        symbol = ['arrow', 'arrow'];
                        symbolSize = [5, 5];
                    }
                    else {
                        symbol = ['none', 'arrow'];
                        symbolSize = [0, 5];
                    }
                    let graphEdgeOption = {
                        lineStyle: this.nodeConnectionStyle,
                        label: label,
                        source: node.url,
                        target: node.intLinks[i],
                        symbol: symbol,
                        symbolSize: symbolSize,
                        value: 3
                    };
                    linkData.push(graphEdgeOption);
                }
            }
        });
        let graphData = new GraphData(data, linkData);
        return graphData;
    }
    calculatePositions(websiteReg) {
        websiteReg.map.forEach((value, key) => {
            let root = value.map.get(value.rootName);
            let rootPosition = this.calculatePosition(0, 1, 1, 0, Math.PI * 2, value.offset);
            let rootSize = this.getSymbolSize(root.depth, Math.PI * 2);
            root.nodePositionalInformation = { position: rootPosition, symbolSize: rootSize, rangeFrom: 0, rangeTo: Math.PI * 2 };
            let visited = [];
            let queue = [];
            queue.push(root);
            while (queue.length > 0) {
                let currentNode = queue.shift();
                visited.push(currentNode);
                let domChilds = currentNode.domChilds;
                let radiansPerChild = (currentNode.nodePositionalInformation.rangeTo - currentNode.nodePositionalInformation.rangeFrom) / domChilds.length;
                let currentRadians = 0;
                for (let i = 0; i < domChilds.length; i++) {
                    let n = value.map.get(domChilds[i]);
                    if (!visited.includes(n)) {
                        let symbolSize = this.getSymbolSize(n.depth, radiansPerChild);
                        let nodePosition = this.calculatePosition(n.depth, i, domChilds.length, currentNode.nodePositionalInformation.rangeFrom, currentNode.nodePositionalInformation.rangeTo, value.offset);
                        let rangeFrom = currentNode.nodePositionalInformation.rangeFrom + currentRadians - (radiansPerChild * 0.5);
                        currentRadians += radiansPerChild;
                        let rangeTo = currentNode.nodePositionalInformation.rangeFrom + currentRadians - (radiansPerChild * 0.5);
                        n.nodePositionalInformation =
                            {
                                position: nodePosition,
                                symbolSize: symbolSize,
                                rangeFrom: rangeFrom,
                                rangeTo: rangeTo
                            };
                        queue.push(n);
                    }
                }
            }
        });
    }
    onChartMouseOver(ec) { }
    onChartClick(ec) {
        let n = this.websiteRegister.getNode(ec.name);
        if (n != undefined) {
            this.selectedNodeName = n.url;
            this.showGraph();
            this.nodeInfoComponent.node = n;
            this.nodeInfoComponent.initializeTable();
            return;
        }
    }
    showAllConnections() {
        this.allConnectionsActive = !this.allConnectionsActive;
        this.showGraph();
    }
    showConnections() {
        this.domConnectionsActive = !this.domConnectionsActive;
        this.showGraph();
    }
    showLabels() {
        this.labelsActive = !this.labelsActive;
        this.showGraph();
    }
    deselectAll() {
        //this.showIntMedia(this.website.internalMedia);
    }
    getOffset(websiteIndex, total) {
        let frac = (websiteIndex / total) * TAU;
        let x = Math.cos(frac) * websiteSpacing;
        let y = Math.sin(frac) * websiteSpacing;
        return new V2(x, y);
    }
    prepareWebsite(name, data, websiteIndex, total) {
        let dictElements = JSON.parse(data);
        let nodeMap = new Map();
        let rootName = "";
        let mediaIntGlobal = [];
        for (let index = 0; index < dictElements.length; index++) {
            const element = dictElements[index];
            //root
            if (index == 0) {
                rootName = element.url;
            }
            let n = new NodeFromJson();
            if (element.url == undefined) {
                continue;
            }
            n.url = element.url;
            n.parentDocId = element.parentDocId;
            n.docId = element.docId;
            n.extMedia = element['link:ext:media'];
            n.intMedia = element['link:int:media'];
            n.extLinks = element['link:ext'];
            n.intLinks = element['link:int'];
            n.getDepth();
            n.getDOMParent();
            if (n.depth >= 0) {
                nodeMap.set(n.url, n);
            }
        }
        nodeMap.forEach((node, key) => {
            if (node.domParent != "") {
                let parent = nodeMap.get(node.domParent);
                if (parent != undefined) {
                    parent.domChilds.push(key);
                }
            }
        });
        let website = new WebsiteMap(name, nodeMap, mediaIntGlobal);
        website.rootName = rootName;
        website.offset = this.getOffset(websiteIndex, total);
        this.websiteRegister.map.set(name, website);
        //return website;
    }
    /**
     * The method fetches, saves and prepares the data for the Creation of the Website Graph. Approximately 200 Nodes,
     * that were crawled by the Worker and saved into Redis, get fetched. The data is saved into dictionaries, adapting to
     * the Data structure that was created in the Backend. Finally, the Nodes for the Graph are created.
     */
    async initRealData() {
        /*
        let nodeMap : Map<string, NodeFromJson> = new Map<string, NodeFromJson>();
        this.loading = true;
        this.errorMessage = "";
        this.websiteService.getWebsiteNodes(this.websiteNameForGraph)
          .subscribe(
            response => {                           //next() callback
              console.log('response received')
              //this.websites = response;
              let dictElements : {[index:string] : string}[] = JSON.parse(JSON.stringify(response))
         
              for (let index = 0; index < dictElements.length; index++) {
                const element  : {[index:string] : string} = dictElements[index];
          
                let pdi : string = "";
                let di : string = "";
                let url : string = "";
                let linksInt : string[] = [];
                let mediaInt : string[] = [];
                let linksExt : string[] = [];
                let mediaExt : string[] = [];
          
                Object.entries(element).forEach(
                  ([key, value]) =>
                  {
                    if(key.includes('parentDocId'))
                    {
                      pdi = value;
                    }
                    else if(key.includes('docId'))
                    {
                      di = value;
                    }
                    else if(key.includes(':int:media:'))
                    {
                      mediaInt.push(value);
                    }
                    else if(key.includes(':int:'))
                    {
                      linksInt.push(value);
                    }
                    else if(key.includes(':ext:media:'))
                    {
                      mediaExt.push(value);
                    }
                    else if(key.includes(':ext:'))
                    {
                      linksExt.push(value);
                    }
                    else if(key.includes('url'))
                    {
                      url = value;
                    }
                    
                  }
                );
                
                let n : NodeFromJson = new NodeFromJson();
                n.url = url;
                n.parentDocId = pdi;
                n.docId = di;
                n.extMedia = mediaExt;
                n.intMedia = mediaInt;
                n.extLinks = linksExt;
                n.intLinks = linksInt;
          
                n.getDepth();
                n.getDOMParent();
                if(n.depth >= 0)
                {
                  nodeMap.set(n.url, n);
                }
              }
          
              nodeMap.forEach((node: NodeFromJson, key: string) =>
              {
                if(node.domParent != "")
                {
                  let parent = nodeMap.get(node.domParent);
                  if(parent != undefined)
                  {
                    parent.domChilds.push(key);
                  }
                }
              });
            console.log("NodeMap: ")
            console.log(nodeMap)
            console.log("End reached")
            this.nodeMap = nodeMap;
          
            },
            (error) => {                              //error() callback
              console.error('Request failed with error')
              this.errorMessage = error;
              this.loading = false;
            },
            () => {                                   //complete() callback
              console.error('Request completed')      //This is actually not needed
              this.loading = false;
            })
