    remap(n, oldFrom, oldTo, newFrom, newTo) {
        let oldRange = oldTo - oldFrom;
        let proportion = (n - oldFrom) / oldRange;
        let newRange = newTo - newFrom;
        return newFrom + newRange * proportion;
    }
    calculatePosition(depth, index, total, rangeFrom, rangeTo) {
        let radians = (total == 1) ? 0.5 * 2 * Math.PI : (index / total) * 2 * Math.PI;
        let radiansRemapped = this.remap(radians, 0, Math.PI * 2, rangeFrom, rangeTo);
        let x = Math.cos(radiansRemapped) * depth * this.radiusPerIteration;
        let y = Math.sin(radiansRemapped) * depth * this.radiusPerIteration;
        let pos = new V2(x, y);
        return this.calculateOffset(pos);
    }
    calculateOffset(pos) {
        return new V2(pos.x + this.halfWidth, pos.y + this.halfHeight);
    }
    getSymbolSize(currentDepth) {
        return 20 - (currentDepth * 3);
    }
    symbolCallback(rawValue, params) {
        console.log("Test");
        return 1;
    }
    calculatePositions(website, root) {
        let data = [];
        let linkData = [];
        let rootPosition = this.calculatePosition(0, 1, 1, root.rangeFrom, root.rangeTo);
        let rootNodeOptions = {
            name: root.url,
            x: rootPosition.x,
            y: rootPosition.y,
            category: 'root',
            symbol: 'circle',
            symbolSize: this.getSymbolSize(root.depth)
        };
        data.push(rootNodeOptions);
        let visited = [];
        let queue = [];
        queue.push(root);
        while (queue.length > 0) {
            let currentNode = queue.shift();
            visited.push(currentNode);
            let links = currentNode.domChilds;
            let radiansPerChild = (currentNode.rangeTo - currentNode.rangeFrom) / links.length;
            let currentRadians = 0;
            for (let i = 0; i < links.length; i++) {
                let n = website.map.get(links[i]);
                let graphEdgeLineStyleOption = {
                    curveness: 0,
                    width: 3,
                    color: 'blue',
                    opacity: 0.8,
                    type: 'solid',
                    cap: 'square',
                    join: 'bevel',
                    dashOffset: 10,
                    miterLimit: 10,
                };
                let label = {
                    position: 'start',
                    distance: 10
                };
                let doubleSided = false;
                /*
                n.internal_links.forEach(element => {
                  if(element == currentNode.url)
                  {
                    doubleSided = true;
                  }
                });
                */
                let symbol = [];
                let symbolSize = [];
                if (doubleSided) {
                    symbol = ['arrow', 'arrow'];
                    symbolSize = [10, 10];
                }
                else {
                    symbol = ['none', 'arrow'];
                    symbolSize = [0, 10];
                }
                let graphEdgeOption = {
                    lineStyle: graphEdgeLineStyleOption,
                    label: label,
                    source: currentNode.url,
                    target: n.url,
                    symbol: symbol,
                    symbolSize: symbolSize,
                    value: 3
                };
                linkData.push(graphEdgeOption);
                if (!visited.includes(n)) {
                    //let domName = n.getDOMStripedName();
                    let nodePosition = this.calculatePosition(n.depth, i, links.length, currentNode.rangeFrom, currentNode.rangeTo);
                    let nodeItemOptions = {
                        name: n.url,
                        x: nodePosition.x,
                        y: nodePosition.y,
                        category: 'not root',
                        draggable: true,
                        symbol: 'circle',
                        symbolSize: this.getSymbolSize(n.depth),
                        symbolOffset: 0,
                        symbolRotate: this.symbolCallback("", "")
                    };
                    data.push(nodeItemOptions);
                    n.rangeFrom = currentNode.rangeFrom + currentRadians - radiansPerChild * 0.5;
                    currentRadians += radiansPerChild;
                    n.rangeTo = currentNode.rangeFrom + currentRadians - radiansPerChild * 0.5;
                    queue.push(n);
                }
            }
        }
        let graphData = new GraphData(data, linkData);
        return graphData;
    }
